package back_tracking;import javax.swing.plaf.basic.BasicBorders;import java.io.BufferedReader;import java.io.IOException;import java.io.InputStreamReader;import java.util.StringTokenizer;public class B15649 {    static int[] output;    static boolean[] visit;    public static void main(String[] args) throws IOException {        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));        StringTokenizer st = new StringTokenizer(br.readLine(), " ");        int n = Integer.parseInt(st.nextToken());        int r = Integer.parseInt(st.nextToken());        visit = new boolean[n + 1];        output = new int[r + 1];        perm(output, visit, 0, n, r);    }    static void perm(int[] output, boolean[] visit, int depth, int n, int r) {        if (depth == r) {            print(output, r);            return;        }        for (int i = 1; i <= n; i++) {            if (!visit[i - 1]) {                visit[i - 1] = true;                output[depth] = i;                perm(output, visit, depth + 1, n, r);                visit[i - 1] = false;            }        }    }    static void print(int[] output, int r) {        for (int i = 0; i < r; i++) {            System.out.print(output[i] + " ");        }        System.out.println();    }}