package back_tracking;import java.util.*;import java.io.*;public class B15650 {    static int[] arr;    static int N, R;    public static void main(String[] args) throws IOException {        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));        StringTokenizer st = new StringTokenizer(br.readLine(), " ");        N = Integer.parseInt(st.nextToken());        R = Integer.parseInt(st.nextToken());        arr = new int[N];        perm(1, 0);    }    public static void perm(int at, int depth) {        if(depth == R) {            for(int i = 0; i < R; i++) {                System.out.print(arr[i] + " ");            }            System.out.println();        }        for(int i = at; i <= N; i++) {            arr[depth] = i;            perm(i + 1, depth + 1);        }    }}