package bruteforce;import java.io.BufferedReader;import java.io.IOException;import java.io.InputStreamReader;public class B1543 {    static int count = 0;    public static void main(String[] args) throws IOException {        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));        String words = br.readLine();        String find = br.readLine();        recursion(0, words, find);        System.out.println(count);    }    static void recursion(int index, String words, String find) {        String newWords = words.substring(index);        if (words.length() <= index) {            return;        }        if (newWords.startsWith(find)) {            count++;            recursion(index + find.length(), words, find);        } else {            recursion(index + 1, words, find);        }    }}