package bruteforce;import java.io.BufferedReader;import java.io.InputStreamReader;import java.util.StringTokenizer;public class getPrimeNum {    public static void main(String[] args) throws Exception {        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));        StringTokenizer st = new StringTokenizer(br.readLine(), "");        int N = Integer.parseInt(st.nextToken());        System.out.println(isPrimeWithSqrt(N));    }    public static int isPrimeWithSqrt(int N) {        int count = 0;        for (int i = 2; i <= N; i++) {            boolean flag = true;            for (int j = 2; j <= Math.sqrt(i); j++) {                if (i % j == 0) {                    flag = false;                    break;                }            }            if (flag == true) {                count++;            }        }        return count;    }}