package dp;import java.io.BufferedReader;import java.io.IOException;import java.io.InputStreamReader;import java.util.Arrays;import java.util.Stack;import java.util.StringTokenizer;// 시간 초과public class B10942 {    /*    1. 테이블 정의        홍준이가 질문한 S,E 범위의 수가 팰린드롬인지 판별    2. 점화식    3. 초기값    */    static StringTokenizer st;    static Stack<Integer> stack;    static int[] copy;    static int S, E;    public static void main(String[] args) throws IOException {        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));        // 수열 크기        int N = Integer.parseInt(br.readLine());        int[] arr = new int[N];        // 칠판에 적은 수        st = new StringTokenizer(br.readLine());        for (int i = 0; i < arr.length; i++) {            arr[i] = Integer.parseInt(st.nextToken());        }        // 홍준이 질문        int M = Integer.parseInt(br.readLine());        for (int i = 0; i < M; i++) {            st = new StringTokenizer(br.readLine());            stack = new Stack<>();            S = Integer.parseInt(st.nextToken());            E = Integer.parseInt(st.nextToken());            copy = Arrays.copyOfRange(arr, S - 1, E);            Arrays.sort(copy);            Arrays.stream(copy).forEach(num -> {                if (stack.isEmpty()) {                    stack.push(num);                } else if (stack.peek() == num) {                    stack.pop();                } else {                    stack.push(num);                }            });            if (stack.size() == 1) {                System.out.println(1);            } else {                System.out.println(0);            }        }    }}