package dp;import java.io.*;public class B11726 {    static int[] d = new int[1001];    public static void main(String[] args) throws IOException {        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));        int X = Integer.parseInt(br.readLine());        System.out.println(dp(X) / 10007);    }    public static int dp(int x) {        if (x == 1) return 1;        if (x == 2) return 2;        if (d[x] != 0) return d[x];        return d[x] = dp(x - 1) + dp(x - 2);    }}