package dp;import java.io.BufferedReader;import java.io.IOException;import java.io.InputStreamReader;public class B1463 {    static int X;    static Integer[] d;    public static void main(String[] args) throws IOException {        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));        X = Integer.parseInt(br.readLine());        d = new Integer[X + 1];        d[0] = 0;        d[1] = 0;        System.out.println(dp(X));    }    public static int dp(int X) {        if (d[X] == null) {            if (X % 6 == 0) {                d[X] = Math.min(dp(X - 1), Math.min(dp(X / 3), dp(X / 2))) + 1;            } else if (X % 3 == 0) {                d[X] = Math.min(dp(X - 1), dp(X / 3)) + 1;            } else if (X % 2 == 0) {                d[X] = Math.min(dp(X - 1), dp(X / 2)) + 1;            } else {                d[X] = dp(X - 1) + 1;            }        } else {            return d[X];        }        return d[X];    }}