package dp;import java.io.BufferedReader;import java.io.IOException;import java.io.InputStreamReader;import java.util.ArrayList;import java.util.List;public class B9095 {    static int N;    static int[] dp;    public static void main(String[] args) throws IOException {        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));        int loop = Integer.parseInt(br.readLine());        int max = 0;        List<Integer> list = new ArrayList<>();        for (int i = 0; i < loop; i++) {            N = Integer.parseInt(br.readLine());            list.add(N);            if (max < N) {                max = N;            }        }        dp = new int[max + 1];        dp[1] = 1;        dp[2] = 2;        dp[3] = 4;        for (int j = 4; j <= max; j++) {            dp[j] = dp[j - 1] + dp[j - 2] + dp[j - 3];        }        for (int i : list) {            System.out.println(dp[i]);        }    }}