package problem;import java.io.BufferedReader;import java.io.InputStreamReader;public class B10872 {    public static void main(String[] args) throws Exception {        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));        int N = Integer.parseInt(br.readLine());        int result = 1;        for (int i = N; i > 0; i--) {            result *= i;            if (N <= 1) {                result = 1;            }        }        System.out.println(result);    }    public static int factorialNum(int number) {        number = number * number - 1;        if (number > 1) {            factorialNum(number);        } else {            number = number * 1;        }        return number;    }}