package problem;import java.io.*;public class B2920 {    static String[] given;    static int plusCount = 1;    static int minusCount = 1;    public static void main(String[] args) throws IOException {        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));        given = br.readLine().split(" ");        for (int i = 0; i < 7; i++) {            if (Integer.parseInt(given[i]) + 1 == Integer.parseInt(given[i + 1])) {                plusCount++;            }            if (Integer.parseInt(given[i]) == Integer.parseInt(given[i + 1]) + 1) {                minusCount++;            }        }        if (plusCount == 8) {            System.out.println("ascending");            return;        }        if (minusCount == 8) {            System.out.println("descending");            return;        }        System.out.println("mixed");    }}