package problem;import java.util.Scanner;public class P11720 {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        int N = sc.nextInt();        String answer = sc.next();        int intAnswer = 0;        for (int i = 0; i < N; i++) {            intAnswer += answer.charAt(i) - '0';        }        System.out.println(intAnswer);    }}