package problem;import java.util.Arrays;import java.util.Scanner;public class P1546 {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        int M = sc.nextInt();        double[] arr = new double[M];        for (int i = 0; i < arr.length; i++) {            arr[i] = sc.nextInt();        }        sc.close();        Arrays.sort(arr);        double highScore = arr[arr.length - 1];        double avg = 0;        for (int i = 0; i < arr.length; i++) {            arr[i] = arr[i] / highScore * 100;            avg += arr[i];        }        System.out.println(avg / M);    }}