package problem;import java.util.Scanner;public class P4344 {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        int C = sc.nextInt();        int[] results;        for (int i = 0; i < C; i++) {            int N = sc.nextInt();            results = new int[N];            double total = 0;            for (int j = 0; j < N; j++) {                int value = sc.nextInt();                results[j] = value;                total += value;            }            double result = (total / N);            double count = 0;            for (int j = 0; j < N; j++) {                if (results[j] > result) {                    count++;                }            }            System.out.printf(String.format("%.3f%%\n", (count / N)*100));        }    }}