package programmers;import java.util.*;public class BiggestNumber {    public String solution(int[] numbers) {        StringBuilder answer = new StringBuilder();        String[] arr = Arrays.stream(numbers)                .mapToObj(String::valueOf)                .toArray(String[]::new);        Arrays.sort(arr, new Comparator<String>() {            @Override            public int compare(String o1, String o2) {                return (o1 + o2).compareTo(o2 + o1);            }        });        if (arr[arr.length - 1].equals("0")) return "0";        for (int i = arr.length - 1; i >= 0; i--) {            answer.append(arr[i]);        }        return answer.toString();    }}