package programmers;public class GuessOfCollatz {    public int solution(int num) {        int answer = 0;        if (num == 1) {            return 0;        }        answer = getCount(num);        return answer;    }    public int getCount(int n) {        int count = 0;        for (int i = 1; i <= 500; i++) {            if (n % 2 == 0) {                n /= 2;            } else if (n % 2 == 1) {                n *= 3;                n += 1;            }            count = i;            if (count == 500) {                return -1;            }            if (n == 1) {                return count;            }        }        return count;    }}