package programmers;import java.util.*;public class HallOfFame {    public int[] solution(int k, int[] score) {        int[] answer = new int[score.length];        List<Integer> hallOfFame = new ArrayList<>();        for (int i = 0; i < score.length; i++) {            if (i < k) {                if (hallOfFame.size() <= k) {                    hallOfFame.add(score[i]);                    answer[i] = getMin(hallOfFame);                }            } else {                if (getMin(hallOfFame) <= score[i] || getMax(hallOfFame) <= score[i]) {                    hallOfFame.remove(hallOfFame.indexOf(getMin(hallOfFame)));                    hallOfFame.add(score[i]);                    answer[i] = getMin(hallOfFame);                } else {                    answer[i] = getMin(hallOfFame);                }            }        }        return answer;    }    public int getMin(List<Integer> arr) {        int min = arr.get(0);        for (int i = 1; i < arr.size(); i++) {            if (min > arr.get(i)) {                min = arr.get(i);            }        }        return min;    }    public int getMax(List<Integer> arr) {        int max = arr.get(0);        for (int i = 1; i < arr.size(); i++) {            if (max < arr.get(i)) {                max = arr.get(i);            }        }        return max;    }}