package programmers;import java.util.*;public class HateSameNumber {    public int[] solution(int[] arr) {        List<Integer> list = new ArrayList<>();        int nowValue = -1;        for (int i = 0; i < arr.length; i++) {            if (arr[i] != nowValue) {                nowValue = arr[i];                list.add(nowValue);            }        }        int[] answer = new int[list.size()];        for (int i = 0; i < list.size(); i++) {            answer[i] = list.get(i);        }        return answer;    }    public static void main(String[] args) {        HateSameNumber h = new HateSameNumber();        int[] arr = {1, 1, 3, 3, 0, 1, 1};        for(int i : h.solution(arr)) {            System.out.println(i);        }    }}