package programmers;import java.util.*;public class JadenCase {    public String solution(String s) {        String answer = "";        String[] arr = s.split(" ");        List<String> list = new ArrayList<>();        StringBuilder sb = new StringBuilder();        for (String str : arr) {            if (!str.equals("")) {                list.add(str);            }        }        for (String str : list) {            String lower = String.valueOf(str.charAt(0));            str = str.toLowerCase();            sb.append(str.replaceFirst(lower, lower.toUpperCase()) + " ");        }        sb.deleteCharAt(sb.length() - 1);        return sb.toString();    }    public static void main(String[] args) {        JadenCase jc = new JadenCase();        System.out.println(jc.solution("3people unFollowed me"));    }}