package programmers;import java.util.ArrayList;import java.util.Arrays;import java.util.List;public class MaxAndMin {    public static void main(String[] args) {        String s = "1 2 3 4";        String[] arr = s.split(" ");        int[] nums = new int[arr.length];        for (int i = 0; i < arr.length; i++) {            nums[i] = Integer.parseInt(arr[i]);        }        Arrays.sort(nums);        final int MIN_NUM = nums[0];        final int MAX_NUM = nums[nums.length - 1];        String answer = MIN_NUM + " " + MAX_NUM;    }}