package programmers;public class NumStrAndAlphabet {    static int solution(String s) {        String[] arr = {"zero", "one", "two", "three", "four", "five", "six", "seven", "eight", "nine"};        for (int i = 0; i < 10; i++) {            s = s.replace(arr[i], Integer.toString(i));        }        System.out.println(s);        return Integer.parseInt(s);    }    public static void main(String[] args) {        solution("one4seveneight");    }}