package programmers;import java.util.PriorityQueue;public class Process {    class Solution {        public int solution(int[] priorities, int location) {            int answer = 0;            PriorityQueue<Integer> queue = new PriorityQueue();            for (int i : priorities) {                queue.add(i);            }            while (!queue.isEmpty()) {                for (int i = 0; i < priorities.length; i++) {                    if (priorities[i] == queue.peek()) {                        if (i == location) {                            answer++;                            return answer;                        }                        queue.poll();                        answer++;                    }                }            }            return answer;        }    }}