package programmers;import java.util.*;public class RightBracket {    boolean solution(String s) {        boolean answer = true;        Stack<Character> stack = new Stack<Character>();        if (s.charAt(0) == ')') {            return false;        }        for (int i = 0; i < s.length(); i++) {            if (s.charAt(i) == '(') {                stack.push(s.charAt(i));            } else {                if (stack.isEmpty()) {                    return false;                } else {                    stack.pop();                }            }        }        answer = stack.isEmpty() ? true : false;        return answer;    }}