package programmers;import java.util.*;class StockPrice {    static int now;    public int[] solution(int[] prices) {        int[] answer = {};        Queue<Integer> queue = new LinkedList<>();        List<Integer> list = new ArrayList<>();        for(int price : prices) {            queue.add(price);        }        while(!queue.isEmpty()) {            int count = 0;            int now = queue.poll();            if(queue.isEmpty()) {                list.add(count);                break;            }            Iterator<Integer> iter = queue.iterator();            while(iter.hasNext()) {                int next = iter.next();                count++;                if(now > next) {                    break;                }            }            list.add(count);        }        return list.stream().mapToInt(Integer::intValue).toArray();    }}