package programmers;public class ToBinary {    public int[] solution(String s) {        int[] answer = new int[2];        isOne(answer, s);        return answer;    }    public String isOne(int[] arr, String s) {        if (s.equals("1")) {            return s;        }        for (String str : s.split("")) {            if (str.equals("0")) {                arr[1]++;                s = s.replaceFirst("0", "");            }        }        arr[0]++;        s = Integer.toBinaryString(s.length());        isOne(arr, s);        return s;    }    public static void main(String[] args) {        ToBinary t = new ToBinary();        int[] arr = t.solution("110010101001");        for (int i : arr) {            System.out.print(i + ", ");        }    }}