package programmers;import java.util.ArrayList;import java.util.List;public class ToBinary {    public int[] solution(String s) {        int[] answer = new int[2];        while (!s.equals("1")) {            for (int i = 0; i < s.length(); i++) {                if (s.charAt(i) == '0') {                    answer[1]++;                    System.out.println("0제거 개수 : " + answer[1]);                    s = s.replaceFirst("0", "");                    System.out.println(s);                }            }            answer[0]++;            System.out.println("answer[0] : "+answer[0]);            s = Integer.toBinaryString(s.length());        }        return answer;    }    public static void main(String[] args) {        ToBinary t = new ToBinary();        int[] arr = t.solution("110010101001");        for (int i : arr) {            System.out.print(i + ", ");        }    }}