package string;import java.util.Scanner;public class B1264 {    public static void main(String[] args) {        char[] moeum = {'a', 'e', 'i', 'o', 'u'};        Scanner sc = new Scanner(System.in);        for (;;) {            String line = sc.nextLine();            int count = 0;            if (line.equals("#")) {                break;            }            line = line.toLowerCase();            for (int j = 0; j < line.length(); j++) {                for (int k = 0; k < moeum.length; k++) {                    if (line.charAt(j) == moeum[k]) {                        count++;                    }                }            }            System.out.println(count);        }    }}