package string;import java.util.Scanner;public class B2675 {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        int R = sc.nextInt();        for (int i = 0; i < R; i++) {            String S = sc.next();            int repeat = sc.nextInt();            StringBuilder P = new StringBuilder();            for (int j = 0; j < S.length(); j++) {                for (int k = 0; k < repeat; k++) {                    P.append(Character.toString(S.charAt(j)));                }            }            System.out.println(P);        }    }}