package string;import java.util.Scanner;public class B2675 {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        int testCase = sc.nextInt();        for (int i = 0; i < testCase; i++) {            String S = sc.next();            int repeat = sc.nextInt();            for (int j = 0; j < S.length(); j++) {                for (int k = 0; k < repeat; k++) {                    System.out.print(S.charAt(j));                }            }            System.out.println();        }    }}